package io.taesu.kotlinplayground

import io.kotest.matchers.shouldBe
import org.junit.jupiter.api.AfterEach
import org.slf4j.LoggerFactory
import java.io.BufferedInputStream
import java.io.FileOutputStream
import java.io.FileWriter
import java.io.PrintWriter
import java.nio.file.Paths
import java.util.*
import kotlin.io.path.inputStream
import kotlin.io.path.readLines
import kotlin.test.Test

/**
 * Created by itaesu on 2024. 8. 20..
 *
 * @author Lee Tae Su
 * @version kotlin-playground
 * @since kotlin-playground
 */
class Base64CodecTest {
    private val log = LoggerFactory.getLogger(this::class.java)
    private val codec = Base64Codec()
    private val path = Paths.get("src/test/resources")

    @Test
    fun `InputStream을 사용하는 경우 Base64 인코딩이 올바르지 않을 수 있다`() {
        // given
        (1..5).forEach {
            log.info("Start test step $it")
            val encodedFile = path.resolve("result$it.txt")
            val targetFile = path.resolve("test.pdf")
            targetFile.inputStream().use { input ->
                PrintWriter(encodedFile.toFile()).use { pw ->
                    codec.getBase64File(encodedFile, input, pw)
                }
            }
        }

        // when
        val base64EncodingStrings = mutableSetOf<String>()
        (1..5).forEach {
            val encodedFile = path.resolve("result$it.txt")
            base64EncodingStrings += encodedFile.readLines().joinToString("")
        }

        // then
        base64EncodingStrings.size shouldBe 1
    }

    @Test
    fun `BufferedInputStream을 사용하는 경우 Base64 인코딩이 올바르다`() {
        // given
        (1..5).forEach {
            log.info("Start test step $it")
            val encodedFile = path.resolve("buf_result$it.txt")
            val targetFile = path.resolve("2_0_Literature Reference(s).pdf")
            targetFile.inputStream().use { input ->
                BufferedInputStream(input).use { binput ->
                    PrintWriter(encodedFile.toFile()).use { pw ->
                        codec.getBase64File(encodedFile, binput, pw)
                    }
                }
            }
        }

        // when
        val base64EncodingStrings = mutableSetOf<String>()
        (1..5).forEach {
            val encodedFile = path.resolve("buf_result$it.txt")
            base64EncodingStrings += encodedFile.readLines().joinToString("")
        }

        // then
        base64EncodingStrings.size shouldBe 1
    }

    @Test
    fun `올바른 base64 인코딩 결과 문자열과 직접 전체 바이트를 인코딩한 결과는 동일하다 `() {
        // given (올바른 base64 인코딩 결과)
        val value =
            """"""

        // when
        val decoded = codec.decode(value)

        // then
        FileOutputStream(path.resolve("resul.pdf").toFile()).use {
            it.write(decoded)
        }

        value shouldBe Base64.getEncoder()
            .encodeToString(path.resolve("2_0_Literature Reference(s).pdf").toFile().readBytes())

    }


    @AfterEach
    fun tearDown() {
        (1..5).forEach {
            path.resolve("result$it.txt").toFile().delete()
            path.resolve("buf_result$it.txt").toFile().delete()
        }
        path.resolve("result.pdf").toFile().delete()
    }
}
